import React, { Component } from 'react';
import 'whatwg-fetch';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import fire from './fire';

export class Mainpage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            post: [],
            searchValue: ''

        };
        this.searchFilter = this.searchFilter.bind(this);
        this.handleSearch = this.handleSearch.bind(this);

    }

    componentDidMount() {
        let joinedGroup = fire.database().ref('posts');
        joinedGroup.on('value', (snapshot) => {
            let obj = snapshot.val();
            if (obj != null) {
                let objKeys = Object.keys(obj);
                let groupArray = objKeys.map((key) => {
                    let task = obj[key];
                    task.key = key;
                    return task;
                })
                this.setState({ post: groupArray });
                console.log(this.state.post)
            }
        })
    }

    searchFilter (input) {
        if(input != ''){
            let upperInput = input.toUpperCase()
            let updatedPost = []
            for(let eachPost of this.state.post) {
                if (eachPost.title.toUpperCase().includes(upperInput) || eachPost.content.toUpperCase().includes(upperInput)){
                    updatedPost.push(eachPost)
                } 
            }
             this.setState({post:updatedPost})
        }
    }

    handleSearch(event) {
        this.setState({searchValue: event.target.value});
    }


    render() {
        return (
            <main>
                <section>
                    <div className="container container-fluid">
                        <div className="d-flex flex-row justify-content-center">
                            <h1 className="sub-title flex-item">Check out these Huskies!</h1>
                        </div>
                    </div>
                    <div className="wrapper">
                        <div className="search-box">
                            <input type="text" placeholder="Search for a className name" onChange={this.handleSearch} id="search-input" aria-label="Search"></input>
                            {console.log(this.state.searchValue)}
                            <a onClick={this.searchFilter(this.state.searchValue)} className="search-icon" href="index.html" role="button" id="searchicon"><i className="fas fa-search"></i><FontAwesomeIcon icon="search" /></a>

                        </div>
                    </div>
                </section>
                <section className="posts">
                    <div className="row">
                        {this.state.post.map((postList, i) => {
                            return <div key={postList.title, i} className='column index'>
                                <div className='cards'>
                                    <h3 className='cardtitle'>{postList.title}</h3>
                                    <img className='pb-3' src={postList.imageSource} alt={postList.title + 'User Profile Image'}></img>
                                    <p className='cardcontent'>{postList.content}</p>
                                    <Link to={{
                                        pathname: '/postdetail',
                                        data: postList
                                    }}><button className="button fit btn-block" aria-label="Profile detail">Detail</button></Link>
                                </div>
                            </div>
                        })}
                    </div>
                </section>
            </main>
        )
    }
}
