import React, { Component } from 'react';
import fire from 'firebase/app';

export class Mygroup extends Component {
    constructor(props) {
        super(props)
        this.state = {
            postData: []
        }
    }

    componentDidMount() {
        let joinedGroup = fire.database().ref('joinedGroup');
        joinedGroup.on('value', (snapshot) => {
            let obj = snapshot.val();
            if (obj != null) {
                let objKeys = Object.keys(obj);
                let groupArray = objKeys.map((key) => {
                    let task = obj[key];
                    task.key = key;
                    return task;
                })
                this.setState({ postData: groupArray });
            }
        })
    }

    render() {
        if (this.state.postData.length > 0) {
            return (
                <section className="posts">
                    <div className="row">
                        {this.state.postData.map((postList, i) => {
                            return <div key={postList.title, i} className='column index'>
                                <div className='cards'>
                                    <h3 className='cardtitle'>{postList.title}</h3>
                                    <img className='pb-3' src={postList.imageSource} alt={postList.title + 'User Profile Image'}></img>
                                    <p className='cardcontent'>{postList.content}</p>
                                </div>
                            </div>
                        })}
                    </div>
                </section>
            )
        } else {
            return (
                <h1 className='text-center'>No Group Found!</h1>
            )
        }
    }
}
