import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import firebase from 'firebase/app';

export class EditProfile extends Component {
    constructor(props) {
        super(props)
        this.state = {
            name: '',
            email: '',
            phone: '',
            major: '',
            grade: '',
            address: '',
        };
    }

    componentDidMount() {
        let userEmail = firebase.auth().currentUser.email.replace(/[^a-zA-Z0-9]/g, "");
        let profileChild = firebase.database().ref(userEmail+"/profile")
        profileChild.child('name').on('value', (snap) => {
            this.setState({
                name: snap.val()
            });
        });
        profileChild.child('email').on('value', snap => {
            this.setState({
                email: snap.val()
            });
        });
        profileChild.child('phone').on('value', snap => {
            this.setState({
                phone: snap.val()
            });
        });
        profileChild.child('major').on('value', snap => {
            this.setState({
                major: snap.val()
            });
        });
        profileChild.child('grade').on('value', snap => {
            this.setState({
                grade: snap.val()
            });
        });
        profileChild.child('address').on('value', snap => {
            this.setState({
                address: snap.val()
            });
        });
    }

    handleEditProfile = () => {
        let userEmail = firebase.auth().currentUser.email;
        let updateProfile = {
            name: this.state.name,
            email: this.state.email,
            phone: this.state.phone,
            major: this.state.major,
            grade: this.state.grade,
            address: this.state.address,
        }
        let updates= {};
        updates['/profile/'] = updateProfile;
        return firebase.database().ref(userEmail.replace(/[^a-zA-Z0-9]/g, "")).ref('profile').update(updates);
    }

    render() {
        return (
            <section>
                <div className="create-descr">
                    <h1>Edit Profile</h1>
                </div>

                <form id="editProfile" className="form" noValidate>
                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputName">Name</label>
                            <input type="text" className="form-control" name="inputName" id="inputName" placeholder="First name / Last name" value={this.state.name} onChange={e => this.setState({ name: e.target.value })}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputEmail">Email</label>
                            <input type="email" className="form-control" name="inputEmail" id="inputEmail" placeholder="ex. firstlast@gmail.com" value={this.state.email} onChange={e => this.setState({ email: e.target.value })}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputPhone">Phone Number</label>
                            <input type="text" className="form-control" name="inputPhone" id="inputPhone" placeholder="ex. 123-456-7890" value={this.state.phone} onChange={e => this.setState({ phone: e.target.value })}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputMajor">Major</label>
                            <input type="text" className="form-control" name="inputMajor" id="inputMajor" placeholder="ex. Informatics" value={this.state.major} onChange={e => this.setState({ major: e.target.value })}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputGrade">Grade</label>
                            <input type="text" className="form-control" name="inputGrade" id="inputGrade" placeholder="ex. Freshmen" value={this.state.grade} onChange={e => this.setState({ grade: e.target.value })}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputAddress">Address</label>
                            <input type="text" className="form-control" name="inputAddress" id="inputAddress" placeholder="ex. 123 Ave Seattle WA 98015" value={this.state.address} onChange={e => this.setState({ address: e.target.value })}></input>
                        </div>
                    </div>

                    <div className="button-container-create-post">
                        <div className="p2 my-auto">
                            <Link to='/profile'>
                                <li onClick={this.handleEditProfile} className="btn btn-secondary btn-lg" id="updateBtn" role="button">Update</li>
                            </Link>
                        </div>
                    </div>

                </form>
            </section>
        )
    }
}
