import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import firebase from 'firebase/app';

export class Profile extends Component {
    constructor(props) {
        super(props)
        this.state = {
            profile: {
                name: '',
                email: '',
                phone: '',
                major: '',
                grade: '',
                address: ''
            }
        };
    }

    componentDidMount() {
        let user = firebase.auth().currentUser;
        this.setState({
            email: user.email
        })
        let profileChild = firebase.database().ref(user.email.replace(/[^a-zA-Z0-9]/g, "") + "/profile")
        profileChild.child('name').on('value', snap => {
            this.setState({
                name: snap.val()
            });
        });
        profileChild.child('email').on('value', snap => {
            this.setState({
                email: snap.val()
            });
        });
        profileChild.child('phone').on('value', snap => {
            this.setState({
                phone: snap.val()
            });
        });
        profileChild.child('major').on('value', snap => {
            this.setState({
                major: snap.val()
            });
        });
        profileChild.child('grade').on('value', snap => {
            this.setState({
                grade: snap.val()
            });
        });
        profileChild.child('address').on('value', snap => {
            this.setState({
                address: snap.val()
            });
        });
    }

    render() {
        return (
            <section>
                <div className="create-descr">
                    <h1>Profile</h1>
                </div>

                <form id="profile" className="form" noValidate>
                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputName">Name</label>
                            <input type="text" className="form-control" name="inputName" id="inputName" readOnly={true} value={this.state.profile.name}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputEmail">Email</label>
                            <input type="email" className="form-control" name="inputEmail" id="inputEmail" readOnly={true} value={this.state.profile.email}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputPhone">Phone Number</label>
                            <input type="text" className="form-control" name="inputPhone" id="inputPhone" readOnly={true} value={this.state.profile.phone}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputMajor">Major</label>
                            <input type="text" className="form-control" name="inputMajor" id="inputMajor" readOnly={true} value={this.state.profile.major}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputGrade">Grade</label>
                            <input type="text" className="form-control" name="inputGrade" id="inputGrade" readOnly={true} value={this.state.profile.grade}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputAddress">Address</label>
                            <input type="text" className="form-control" name="inputAddress" id="inputAddress" readOnly={true} value={this.state.profile.address}></input>
                        </div>
                    </div>

                    <div className="button-container-create-post">
                        <div className="p2 my-auto">
                            <Link to={{
                                pathname: '/editprofile',
                                state: {
                                    test : true
                                }
                            }}><li className="btn btn-secondary btn-lg" id="editBtn" role="button">Edit</li></Link>
                        </div>
                    </div>
                </form>
            </section>
        )
    }
}
