import React, { Component, useState } from 'react';
import { Link } from 'react-router-dom';

export class Nav extends Component {

    constructor(props){
        super(props)
        this.state={
            hamburgerClicked : false,
            navbarlineClass : "navbar-links"
        }
    }

    clickHandle = () => {
        if (this.state.hamburgerClicked == false){
            this.setState({
                hamburgerClicked : true,
                navbarlineClass : "navbar-links active"
            })
        } else if (this.state.hamburgerClicked == true) {
            this.setState({
                hamburgerClicked : false,
                navbarlineClass : "navbar-links"
            })
        }
    }

    render() {
        return (
            <header>
                <nav className="navbar">
                    <div className="brand-title">
                    <Link to="/"><li className='appName' href="index.html">Husky Finder</li></Link>
                    </div>

                    <a href="#" className="toggle-button" onClick={this.clickHandle}>
                        <span className="bar"></span>
                        <span className="bar"></span>
                        <span className="bar"></span>
                    </a>

                    <div className={this.state.navbarlineClass}>
                        <ul>
                            <Link to="/"><li>Home</li></Link>
                            <Link to='/mygroup'><li>My Group</li></Link>
                            <Link to='/profile'><li>Profile</li></Link>
                            <li><a href="index.html">Sign Out</a></li>
                            <Link to='/Authen'><li>Authen</li></Link>
                        </ul>
                    </div>

                </nav>
                <div className="d-flex flex-row p-3 justify-content-between">
                    <div className="p-2 my-auto">
                        <Link to='/createpost'>
                        <li className="btn btn-success createPostBtn" role="button">Create Post</li>
                        </Link>
                    </div>
                </div>
            </header>
        )
    }
}
