import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import firebase from 'firebase/app';

export class Profile extends Component {
    _isMounted = false;
    constructor(props) {
        super(props)
        this.state = {
            name: '',
            email: '',
            phone: '',
            major: '',
            grade: '',
            address: ''
        };
    }

    componentDidMount() {
        this._isMounted = true;
        let userEmail = firebase.auth().currentUser.email.replace(/[^a-zA-Z0-9]/g, "");
        this.setState({
            email: userEmail
        })
        firebase.database().ref(userEmail+'/profile').on('value', (snapshot) => {
            if (this._isMounted) {
                this.setState({
                    ...(snapshot.val() || {})   
                })
            }
         })
    }
    
    componentWillUnmount() {
        this._isMounted = false;
    }

    render() {
        return (
            <section>
                <div className="create-descr">
                    <h1>Profile</h1>
                </div>

                <form id="profile" className="form" noValidate>
                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputName">Name</label>
                            <input type="text" className="form-control" name="inputName" id="inputName" readOnly={true} value={this.state.name}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputEmail">Email</label>
                            <input type="email" className="form-control" name="inputEmail" id="inputEmail" readOnly={true} value={this.state.email}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputPhone">Phone Number</label>
                            <input type="text" className="form-control" name="inputPhone" id="inputPhone" readOnly={true} value={this.state.phone}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputMajor">Major</label>
                            <input type="text" className="form-control" name="inputMajor" id="inputMajor" readOnly={true} value={this.state.major}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputGrade">Grade</label>
                            <input type="text" className="form-control" name="inputGrade" id="inputGrade" readOnly={true} value={this.state.grade}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputAddress">Address</label>
                            <input type="text" className="form-control" name="inputAddress" id="inputAddress" readOnly={true} value={this.state.address}></input>
                        </div>
                    </div>

                    <div className="button-container-create-post">
                        <div className="p2 my-auto">
                            <Link to={{
                                pathname: '/editprofile'
                            }}><li className="btn btn-secondary btn-lg" id="editBtn" role="button">Edit</li></Link>
                        </div>
                    </div>
                </form>
            </section>
        )
    }
}
