import React, { Component } from 'react';

export class PostDetail extends Component {
    constructor(props){
        super(props);
        this.state= this.props.location.data;
    }
    joinGroup(){
        if(this.state.currentSize < this.state.groupSize){
            this.state.currentSize = this.state.currentSize + 1;
        }
    }
    render() {
        return (
            <section key={this.state.title + this.props.location.content} className="postdetail">
                <h1 className="posttitle">{this.state.title}</h1>
                <div className="postimg">
                    <img className="pb-3" src={this.state.imageSource} alt={this.state.name + "user profile image"} cite={this.state.imageCite}></img>
                </div>
                <section className="postinfo">
                    <p className="name"><strong>Name: </strong>{this.state.name}</p>
                    <p className="email"><strong>Email: </strong>{this.state.email}</p>
                    <p className="postcontent">{this.state.postInfo}</p>
                    <p className="member"><strong>Member: </strong>{this.state.currentSize}/{this.state.groupSize}</p>
                    <button onClick={this.joinGroup} className="respondbtn" role="columnheader" type="button">Join</button>
                </section>
            </section>
        )
    }
}
