import React, { Component, useState } from 'react';
import { Link } from 'react-router-dom';
import firebase from 'firebase/app';
import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth';

export class Nav extends Component {

    constructor(props) {
        super(props)
        this.state = {
            hamburgerClicked: false,
            navbarLinkClass: "navbar-links"
        }
    }

    clickHandle = () => {
        if (this.state.hamburgerClicked == false) {
            this.setState({
                hamburgerClicked: true,
                navbarLinkClass: "navbar-links active"
            })
        } else {
            this.setState({
                hamburgerClicked: false,
                navbarLinkClass: "navbar-links"
            })
        }
    }

    handleSignOut = () => {
        firebase.auth().signOut()
    }

    render() {
        return (
            <header>
                <nav className="navbar">
                    <div className="brand-title">
                        <Link to="/"><li className='appName' href="index.html">Husky Finder</li></Link>
                    </div>

                    <a href="#" className="toggle-button" onClick={this.clickHandle}>
                        <span className="bar"></span>
                        <span className="bar"></span>
                        <span className="bar"></span>
                    </a>

                    <div className={this.state.navbarLinkClass}>
                        <ul>
                            <Link to="/"><li>Home</li></Link>
                            <Link to={{
                                pathname: '/mygroup',
                                useremail: this.props.useremail,
                            }}><li>My Group</li></Link>
                            <Link to={{
                                pathname: '/profile',
                                useremail: this.props.useremail,
                            }}><li>Profile</li></Link>
                            <li onClick={this.handleSignOut}>Sign Out</li>
                        </ul>
                    </div>

                </nav>
                <div className="d-flex flex-row p-3 justify-content-between">
                    <div className="p-2 my-auto">
                        <Link to={{
                            pathname: '/createpost',
                            useremail: this.props.useremail,
                        }}><li className="btn btn-success createPostBtn" role="button">Create Post</li></Link>
                    </div>
                </div>
            </header >
        )
    }
}
