import React, { Component } from 'react';
import 'whatwg-fetch';
import { SearchBar } from './SearchBar';
import { BrowserRouter as Router, Route, Link} from 'react-router-dom'


export class Mainpage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            post: []
        };
    }

    componentDidMount() {
        fetch("./data/postData.json")
            .then((res) => res.json())
            .then((data) => {
                this.setState({ post: data })
            })
    }

    handleClick(){
        
    }

    renderPost(postList) {
        return (
            <div key={postList.title + postList.content} className='column index'>
                <div className='cards'>
                    <h3 className='cardtitle'>{postList.title}</h3>
                    <img className='pb-3' src={postList.imageSource} alt={postList.title + 'User Profile Image'}></img>
                    <p className='cardcontent'>{postList.content}</p>
                    <a href="index.html" className="button fit btn-block" aria-label="Profile detail">Detail</a>
                </div>
            </div>
        )
    }

    render() {
        return (
            <main>
                <SearchBar />
                <section className="posts">
                    <div className="row">
                        {this.state.post.map((postData) => {
                            return this.renderPost(postData);
                        })}
                    </div>
                </section>
            </main>
        )
    }
}
