import React, { Component } from 'react';
import 'whatwg-fetch';

export class Showpost extends Component {
    constructor(props) {
        super(props);
        this.state = {
            post: []
        };
    }

    renderPost(postArray) {
        let post = document.querySelector(".posts .row");
        for (let i of postArray) {
            let result = document.createElement("div");
            result.classList = "column index";
            let card = document.createElement("div");
            card.classList = "cards";
            result.appendChild(card);
            let title = document.createElement("h3");
            title.classList = "cardtitle";
            title.textContent = i.title;
            card.appendChild(title);
            let image = document.createElement("img");
            image.classList = "pb-3";
            image.src = i.imageSource;
            image.alt = "User profile image";
            image.cite = i.imageCite;
            card.appendChild(image);
            let content = document.createElement("p");
            content.classList = "cardcontent";
            content.textContent = i.content;
            card.appendChild(content);
            let button = document.createElement("a");
            button.href = "#";
            button.classList = "button fit btn-block";
            button.role = "button";
            button.setAttribute("aria-label", "Profile detail");
            button.textContent = "Detail";
            card.appendChild(button);
            post.appendChild(result);
        }
    }

    componentDidMount() {
        fetch("data/postData.json")
            .then(function (response) {
                return response.json();
            })
            .then(function (data) {
                let state = []
                for (let i of data) {
                    state.push(i);
                }
                this.setState({ post: state })
                this.renderPost(data);
            })
    }



    render() {
        return (
            <section className="posts">
                <div className="row">
                    {this.componentDidMount}
                </div>
            </section>
        )
    }
}
