import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { faFire } from '@fortawesome/free-solid-svg-icons';
import firebase from 'firebase/app';

export class Profile extends Component {
    constructor(props) {
        super(props)
        this.state = {
            name: '',
            email: '',
            phone: '',
            major: '',
            grade: '',
            address: '',
        };
    }
    
    componentDidMount() {


        this.authUnSubFunction = firebase.auth().onAuthStateChanged((firebaseUser) => {
            if(firebaseUser) { 
              console.log("Logged in as", firebaseUser.name);
              this.setState({name: firebaseUser.name,
                email: firebaseUser.email,
                phone: firebaseUser.phone,
                major: firebaseUser.major,
                grade: firebaseUser.grade,
                address: firebaseUser.address
            })
            } else {
              console.log("Logged out");
              this.setState({name: null,
                email: null,
                phone: null,
                major: null,
                grade: null,
                address: null})
            }
        })


        let profileChild = firebase.database().ref().child('profile');
        profileChild.child('name').on('value', snap => {
            this.setState({
                name: snap.val()
            });
        });
        profileChild.child('email').on('value', snap => {
            this.setState({
                email: snap.val()
            });
        });
        profileChild.child('phone').on('value', snap => {
            this.setState({
                phone: snap.val()
            });
        });
        profileChild.child('major').on('value', snap => {
            this.setState({
                major: snap.val()
            });
        });
        profileChild.child('grade').on('value', snap => {
            this.setState({
                grade: snap.val()
            });
        });
        profileChild.child('address').on('value', snap => {
            this.setState({
                address: snap.val()
            });
        });
    }

    handleSignIn = () => {
        faFire.auth().signInWithEmailAndPassword(this.state.email, this.state.password)
            .then((userCredential) => {
                let firebaseUser = userCredential.user;
                console.log(firebaseUser);
                this.setState({ user: firebaseUser });
            })
            .catch((err) => {
                this.setState({ errorMessage: err.message });
            })
    }
    handleSignOut = () => {
        firebase.auth().signOut().catch(err => console.log(err)); 
    }

    render() {
        return (
            <main>
                <div className="create-descr">
                    <h1>Profile</h1>
                </div>

                <form id="profile" className="form" noValidate>
                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputName">Name</label>
                            <input type="text" className="form-control" name="inputName" id="inputName" readOnly={true} value={this.state.name}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputEmail">Email</label>
                            <input type="email" className="form-control" name="inputEmail" id="inputEmail" readOnly={true} value={this.state.email}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputPhone">Phone Number</label>
                            <input type="text" className="form-control" name="inputPhone" id="inputPhone" readOnly={true} value={this.state.phone}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputMajor">Major</label>
                            <input type="text" className="form-control" name="inputMajor" id="inputMajor" readOnly={true} value={this.state.major}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputGrade">Grade</label>
                            <input type="text" className="form-control" name="inputGrade" id="inputGrade" readOnly={true} value={this.state.grade}></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputAddress">Address</label>
                            <input type="text" className="form-control" name="inputAddress" id="inputAddress" readOnly={true} value={this.state.address}></input>
                        </div>
                    </div>

                    <div className="button-container-create-post">
                        <div className="p2 my-auto">
                            <Link to='/editprofile'>
                                <li className="btn btn-secondary btn-lg" id="editBtn" role="button">Edit</li>
                            </Link>
                        </div>
                    </div>
                </form>
            </main>
        )
    }
}
