import React, { Component } from 'react';

export class PostDetail extends Component {
    render() {
        return (
            <section key={this.props.data.title + this.props.data.content} class="postdetail">
                <h1 class="posttitle">{this.props.data.title}</h1>
                <div class="postimg">
                    <img class="pb-3" src={this.props.data.imageSource} alt={this.props.data.name + "user profile image"} cite={this.props.data.imageCite}></img>
                </div>
                <section class="postinfo">
                    <p class="name"><strong>Name:</strong>{this.props.data.name}</p>
                    <p class="email"><strong>Email:</strong>{this.props.data.email}</p>
                    <p class="postcontent">{this.props.data.postInfo}</p>
                    <p class="member"><strong>Member</strong>: {this.props.data.currentSize}/{this.props.data.groupSize}</p>
                    <button class="respondbtn" role="columnheader" type="button">Join</button>
                </section>
            </section>
        )
    }
}
