import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import firebase from 'firebase/app';

export class Createpost extends Component {
    constructor(props) {
        super(props)
        this.state = {
            content: '',
            currentSize: 1,
            email: '',
            groupSize: '',
            imageCite: 'https://unsplash.com/s/photos/study',
            imageSource: './img/default.png',
            joinValue: false,
            name: '',
            postInfo: '',
            title: '',
        }

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleName = this.handleName.bind(this);
        this.handleEmail = this.handleEmail.bind(this);
        this.handleTitle = this.handleTitle.bind(this);
        this.handleContent = this.handleContent.bind(this);
        this.handleGroupSize = this.handleGroupSize.bind(this);
        this.handlePostInfo = this.handlePostInfo.bind(this);

    }

    handleName(event) {
        this.setState({name: event.target.value});
    }

    handleEmail(event) {
        this.setState({email: event.target.value});
    }

    handleContent(event) {
        this.setState({content: event.target.value});
    }

    handleGroupSize(event) {
        this.setState({groupSize: event.target.value});
    }

    handlePostInfo(event) {
        this.setState({postInfo: event.target.value});
    }

    handleTitle(event) {
        this.setState({title: event.target.value});
    }

    handleSubmit = () => {
        let postRef = firebase.database().ref('posts')
        postRef.push(this.state)
    
    }

    render() {
        const { name, email, content, title, postInfo } = this.state;
        const isEnabled = email.length > 0 && name.length > 0 && content.length > 0 && title.length > 0 && postInfo.length > 0;
        return (
            <main>
                <div className="create-descr">
                    <h1>Create a Post</h1>
                </div>

                <form id="signUpForm" className="form" noValidate>
                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputName">Name</label>
                            <input type="text" className="form-control" onChange={this.handleName} name="inputName" id="inputName" placeholder="First name / Last name"></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputEmail">Email</label>
                            <input type="email" className="form-control" onChange={this.handleEmail} name="inputEmail" id="inputEmail" placeholder="Your contact email"></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="postTitle">Post Title</label>
                            <input type="text" className="form-control" onChange={this.handleTitle}  name="postTitle" id="postTitle" placeholder="ex. INFO200"></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="postIntro">Post Intro</label>
                            <input type="text" className="form-control" onChange={this.handleContent} name="postIntro" id="postIntro" placeholder="ex. looking for a study group"></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="category">Post Category</label>
                            <select className="postCategory" name="category" id="category">
                                <option value="Study Partner">Study Partner</option>
                                <option value="Study Group">Study Group</option>
                                <option value="Mentor">Mentor</option>
                                <option value="Project Member">Project Member</option>
                            </select>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="groupSize">Group Size (if applicable)</label>
                            <select className="groupSize" onChange={this.handleGroupSize} name="groupSize" id="groupSize">
                                <option value="2">2</option>
                                <option value="3">3</option>
                                <option value="4">4</option>
                                <option value="5">5</option>
                            </select>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="textarea">Post Description</label>
                            <br></br>
                            <textarea rows="5" id="textarea" onChange={this.handlePostInfo} className="textarea" name="textarea" placeholder="Please describe..."></textarea>
                        </div>
                    </div>

                    <div className="button-container-create-post">
                        <div className="p2 my-auto">
                            <Link to='/'>
                                <button className="btn btn-secondary btn-lg" disabled={!isEnabled} onClick={this.handleSubmit} id="submitBtn">Submit</button>
                            </Link>
                        </div>
                        <div className="p-2 my-auto">
                            <Link to='/'>
                                <button className="btn btn-secondary btn-lg">Cancel</button>
                            </Link>
                        </div>
                    </div>
                </form>
            </main>
        )
    }
}
