        return (
            <main>
                <div className="create-descr">
                    <h1>Edit Profile</h1>
                </div>

                <form id="editProfile" className="form" noValidate>
                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputName">Name</label>
                            <input type="text" className="form-control" name="inputName" id="inputName" value="Jason Weng"></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="inputEmail">Email</label>
                            <input type="email" className="form-control" name="inputEmail" id="inputEmail" value="wuyiweng@uw.edu"></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="postTitle">Post Title</label>
                            <input type="text" className="form-control" name="postTitle" id="postTitle" placeholder="ex. INFO200"></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="postIntro">Post Intro</label>
                            <input type="text" className="form-control" name="postIntro" id="postIntro" placeholder="ex. looking for a study group"></input>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="category">Post Category</label>
                            <select className="postCategory" name="category" id="category">
                                <option value="Study Partner">Study Partner</option>
                                <option value="Study Group">Study Group</option>
                                <option value="Mentor">Mentor</option>
                                <option value="Project Member">Project Member</option>
                            </select>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="groupSize">Group Size (if applicable)</label>
                            <select className="groupSize" name="groupSize" id="groupSize">
                                <option value="2">2</option>
                                <option value="3">3</option>
                                <option value="4">4</option>
                                <option value="5">5</option>
                            </select>
                        </div>
                    </div>

                    <div className="form-row justify-content-center">
                        <div className="form-group col-9 col-xl-6">
                            <label htmlFor="textarea">Post Description</label>
                            <br></br>
                            <textarea rows="5" id="textarea" className="textarea" name="textarea" placeholder="Please describe..."></textarea>
                        </div>
                    </div>

                    <div className="button-container-create-post">
                        <div className="p2 my-auto">
                            <li onCall={handleEditProfile(/*一个object存了上面所有input value*/)} className="btn btn-secondary btn-lg" id="updateBtn" role="button">Update</li>
                        </div>
                    </div>
                </form>
            </main>
        )
    }
}
