import React, { Component } from 'react';
import 'whatwg-fetch';

export class Showpost extends Component {
    constructor(props) {
        super(props);
        this.state = {
            post: []
        };
    }

    renderPost(postList) {
        return(
            <div class='column index'>
                <div class='cards'>
                    <h3 class='cardtitle'>{postList.title}</h3>
                    <img class='pb-3' src={postList.imageSource} alt='User Profile Image'></img>
                    <p class='cardcontent'>{postList.content}</p>
                    <a href="#" class="button fit btn-block" aria-label="Profile detail">Detail</a>
                </div>
            </div>
        )
    }
        

    componentDidMount() {
        fetch("data/postData.json")
            .then(function (response) {
                return response.json();
            })
            .then(function (data) {
                let state = []
                for (let i of data) {
                    state.push(i);
                }
                this.setState({post: state})
                console.log(this.state)
            })
    }



    render() {
        return (
            <section className="posts">
                <div className="row">
                    {this.state.post.map((postData) =>{
                        return this.renderPost(postData);
                    })}
                </div>
            </section>
        )
    }
}
