import React, { Component } from 'react';
import fire from './fire';

export class PostDetail extends Component {
    constructor(props){
        super(props);
        this.state= {
            postList : this.props.location.data,
            currSize : Number(this.props.location.data.currentSize),
            maxSize : Number(this.props.location.data.groupSize),
            joinValue: false,
            whetherJoined : false,
            key: this.props.location.data.key
        };
    }

    joinGroup = () => {
        if(this.state.currSize < this.state.maxSize){
            let increasedNum = this.state.currSize + 1;
            this.setState({
                currSize : increasedNum,
                joinValue : true,
                whetherJoined : true
            });
            let joinedGroupRef = fire.database().ref('joinedGroup')
            joinedGroupRef.push(this.state.postList);
            let updatedPost = {
                content: this.state.postList.content,
                currentSize: this.state.currSize,
                email: this.state.postList.email,
                groupSize: this.state.postList.groupSize,
                imageCite: this.state.postList.imageCite,
                imageSource: this.state.postList.imageSource,
                key: this.state.postList.key,
                name: this.state.postList.name,
                postInfo: this.state.postList.postInfo,
                title: this.state.postList.title
            }
            // fire.database().ref().update()
        }
    }

    render() {
        let joinState = ''
        if(this.state.whetherJoined == true){
            joinState = ' (JOINED)'
        }
        return (
            <section key={this.state.postList.title + this.state.postList.content} className="postdetail">
                <h1 className="posttitle">{this.state.postList.title}</h1>
                <div className="postimg">
                    <img className="pb-3" src={this.state.postList.imageSource} alt={this.state.postList.name + "user profile image"} cite={this.state.postList.imageCite}></img>
                </div>
                <section className="postinfo">
                    <p className="name"><strong>Name: </strong>{this.state.postList.name}</p>
                    <p className="email"><strong>Email: </strong>{this.state.postList.email}</p>
                    <p className="postcontent">{this.state.postList.postInfo}</p>
                    <p className="member"><strong>Member: </strong>{this.state.currSize}/{this.state.maxSize +joinState}</p>
                    <button onClick={this.joinGroup} disabled={this.state.joinValue} className="respondbtn" role="columnheader" type="button">Join</button>
                </section>
            </section>
        )
    }
}
