import React, { Component } from 'react';

export class PostDetail extends Component {
    constructor(props){
        super(props);
        this.state= {
            postList : this.props.location.data,
            currSize : Number(this.props.location.data.currentSize),
            maxSize : Number(this.props.location.data.groupSize)
        };
    }
    joinGroup = () => {
        if(this.state.currSize < this.state.maxSize){
            let increasedNum = this.state.currSize + 1;
            this.setState({currSize : increasedNum})
        }
    }
    render() {
        return (
            <section key={this.state.postList.title + this.state.postList.content} className="postdetail">
                <h1 className="posttitle">{this.state.postList.title}</h1>
                <div className="postimg">
                    <img className="pb-3" src={this.state.postList.imageSource} alt={this.state.postList.name + "user profile image"} cite={this.state.postList.imageCite}></img>
                </div>
                <section className="postinfo">
                    <p className="name"><strong>Name: </strong>{this.state.postList.name}</p>
                    <p className="email"><strong>Email: </strong>{this.state.postList.email}</p>
                    <p className="postcontent">{this.state.postList.postInfo}</p>
                    <p className="member"><strong>Member: </strong>{this.state.currSize}/{this.state.maxSize}</p>
                    <button onClick={this.joinGroup} className="respondbtn" role="columnheader" type="button">Join</button>
                </section>
            </section>
        )
    }
}
