import React, { Component } from 'react';
import 'whatwg-fetch';
import { SearchBar } from './SearchBar';
import { Link } from 'react-router-dom';


export class Mainpage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            post : []
        };
    }

    componentDidMount() {
        let joinedGroup = fire.database().ref('posts');
        joinedGroup.on('value', (snapshot) => {
            let obj = snapshot.val();
            if (obj != null) {
                let objKeys = Object.keys(obj);
                let groupArray = objKeys.map((key) => {
                    let task = obj[key];
                    task.key = key;
                    return task;
                })
                this.setState({ post : groupArray });
            }
        })
    }

    render() {
        return (
            <main>
                <SearchBar />
                <section className="posts">
                    <div className="row">
                        {this.state.post.map((postList, i) => {
                            return <div key={postList.title, i} className='column index'>
                                <div className='cards'>
                                    <h3 className='cardtitle'>{postList.title}</h3>
                                    <img className='pb-3' src={postList.imageSource} alt={postList.title + 'User Profile Image'}></img>
                                    <p className='cardcontent'>{postList.content}</p>
                                    <Link to={{
                                        pathname: '/postdetail',
                                        data:postList 
                                        }}><button className="button fit btn-block" aria-label="Profile detail">Detail</button></Link>
                                </div>
                            </div>
                        })}
                    </div>
                </section>
            </main>
        )
    }
}
